// import sttyles from './styles.module.css'
const Items = ({producto, cart, setCart}) => {
    const addToCart = () => {
        setCart([...cart, producto])
    }

    return(
        <div className="card" style={{width:"350px"}}>
            <div>
            <img className="card-img-top" src={producto.image} alt={producto.title} style={{width:"200px"}} />
            </div>
            <div className="card-body">
                <h5 className="card-title">{producto.title}</h5>
                <>${producto.price}</>
                <button className="btn btn-primary" onClick={addToCart}>Agregar</button>
            </div>
        </div>
    )
}

export default Items
