import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import CartWidget from "../cartwidget/CartWidget";
import Items from "../items/Items";

const NavBar = () => {
  const [categories, setCategories] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [categorySelected, setCategorySelected] = useState();
  const [productos, setProductos] = useState([]);
  const {id} = useParams()

  useEffect(() => {
    if(categorySelected){
    fetch(`https://fakestoreapi.com/products/category/${categorySelected}`)
    .then(res=>res.json())
    .then(data => {
        console.log(data)
        setProductos(data)
    })
     .catch(e => console.error(e))
     .finally(() => setCargando(false))
    }
  }, [categorySelected])

  const getByCategory = (cat) => {
    setCategorySelected(cat);
    setCargando(true);

  };

  return (
    <nav className="navbar navbar-expand-lg bg-primary-subtle border-body">
      <div className="container-fluid">
        <Link to="/" className="navbar-brand">
          Tienda online
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link to="/" className="nav-link active" aria-current="page">
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to={pro}
                className="nav-link active"
                aria-current="page"
              >
                Electronica
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to="category/joyas"
                className="nav-link active"
                aria-current="page"
              >
                Joyas
              </Link>
            </li>
            <li className="nav-item">
            <Link
                to="category/ropaHombre"
                className="nav-link active"
                aria-current="page"
              >
                Ropa de hombre
              </Link>
            </li>
            <li className="nav-item">
            <Link
                to="category/ropaMujer"
                className="nav-link active"
                aria-current="page"
              >
               Ropa de Mujer
              </Link>
            </li>
          </ul>
          <div>
          {categories?.map((cat) => <button className="btn btn-outline-dark ms-3" key={cat} onClick={() => getByCategory(cat)}>{cat}</button>)}
          </div>
        </div>
      </div>      
      {<CartWidget />}
    </nav>
  );
};

export default NavBar;
