import { useState, useEffect } from "react";
import { json } from "react-router-dom";

function ItemListContainer () {
    const [productos, setProductos] = useState([])
    const [cargando, setCargando] = useState(true)
    const [categories, setCategories] = useState([])
    const [categorySelected, setCategorySelected] = useState()
    const [cart, setCart] = useState([])

   useEffect(() => {
    fetch('https://fakestoreapi.com/products?limit=1')
    .then(res => res.json())
    .then(data => {
        console.log(data)
        setProductos(data)
    })
    .catch(e => console.error(e))
    .finally(() => setCargando(false))

    fetch('https://fakestoreapi.com/products/categories')
        .then(res => res.json())
        .then(json => console.log(json))
        .catch(e => console.error(e))

   }, [])

   useEffect(() => {
    if(categorySelected) {
        fetch(`https://fakestoreapi.com/products/category/$(categorySelected)`)
        .then(res => res.json())
        .then(data => {
            console.log(data)
            setProductos(data)
        })
        .catch(e => console.error(e))
        .finally(() => setCargando(false))
    }
   }, [categorySelected])

   const gerByCategory = (cat) => {
    setCargando(true)
    setCategorySelected(cat)
   }

   return (
    <div>
        <header>
            <h1>
                Mi Ecommerce en React - Cart: {cart.length}
            </h1>
            <div>
                <div>
                    {categories?.map(cat => {
                        // botones
                    })}
                </div>
                {cargando ? (
                    <p> Cargando productos...</p>
                ) : (
                    // Mi map o componente con map de productos
                    console.log("Map de productos")
                )}
            </div>
        </header>
    </div>
   );
}

export default ItemListContainer
