    const [productoSelected, setProductoSelected] = useState();

    useEffect(() => {
        if(productoSelected) {
            fetch(`https://fakestoreapi.com/products/${productoSelected}`)
            .then(res=>res.json())
            .then(data => {
                setProductoSelected(data)
            })
            .catch(e => console.error(e))
            .finally(() => setCargando(false))
        }
    }, [productoSelected])

