// import sttyles from './styles.module.css'
const Items = ({producto, cart, setCart}) => {
    const addToCart = () => {
        setCart([...cart, producto])
    }

    return(
        <div className="card" style={{width:"18rem"}}>
            <div>
            <img src={producto.image} alt={producto.title} style={{}} />
            </div>
            <div>
                <p>{producto.title}</p>
                <p>${producto.price}</p>
                <button className="" onClick={addToCart}>Agregar</button>
            </div>
        </div>
    )
}

export default Items
