  const {producto, cart, setCart} = useContext(ItemListContainerContext)

  return (
    <div className="container card mb-3" style={{ width: "350px" }}>
      <div>
        <img
          className="card-img-top"
          src={producto.image}
          alt={producto.title}
          style={{ width: "200px" }}
